import { Component, OnInit } from '@angular/core';
import { Patient } from '../interfaces/patient'; 
import { DatePipe } from '@angular/common';
import { FormBuilder, Validators } from '@angular/forms';
import { PatientcrudserviceService } from '../services/patientcrudservice.service';
import Swal from 'sweetalert2';
interface PageEvent {
  first: number;
  rows: number;
  page: number;
  pageCount: number;
}
@Component({
  selector: 'app-patient',
  templateUrl: './patient.component.html',
  styleUrls: ['./patient.component.scss'] ,
  providers:[DatePipe]
})
export class PatientComponent implements OnInit
 {
  patients: Patient[] = [];

  /*------------------------------------------
  --------------------------------------------
  Created constructor
  --------------------------------------------
  --------------------------------------------*/
  constructor(public patientService: PatientcrudserviceService) { }

  /**
   * Write code on Method
   *
   * @return response()
   */
  ngOnInit(): void 
  {
    this.getAllPatients();
  } 

  getAllPatients(){
    this.patientService.getAll().subscribe((data:any)=>
    {
      this.patients = data.data.patients;
      console.log(this.patients);
    });
  }
  delete(id?:number){
    this.patientService.delete(id).subscribe(res => 
      {
         console.log(res);
         console.log('Post deleted successfully!');
    })
  }
}
