<div class="content-body">
    <div class="container">
    <div class="holiday p-3 rounded register-file mt-3">
         <h1 class="fs-20 pb-2  skill-title">Patients</h1>
             <hr>
        <div class="d-flex justify-content-end align-items-center flex-wrap flex-md-nowrap mb-4 mt-3">
            <button href="" class="px-5 py-2 fs-5 btn btn-primary mt-md-0 mt-3 border-0" (click)="openBackDropCustomClass(holiday,null)">create</button>
          <div class="mt-lg-0 mt-3">
            <div class="input-group">
              <!-- <input type="text" class="form-control" placeholder="search" aria-label="Username" aria-describedby="basic-addon1" [(ngModel)]="searchterm" (keyup)="search()"> -->
            </div>
          </div>
        </div>

        <div class="table-content table-responsive">
          <table class="table text-center" id="content">
            <thead>
              <tr class="border-bottom fs-8 text-dark-color">
                <th scope="col" class="text-center">#</th>
                <th scope="col" class="text-center">pasNumber</th>
                <th scope="col" class="text-center">fore names</th>
                <th scope="col" class="text-center">sur name</th>
                <th scope="col" class="text-center">date Of Birth</th>
                <th scope="col" class="text-center">sex Code</th>
                <th scope="col" class="text-center">home TelephoneNumber</th>
                <th scope="col" class="text-center">nok Name</th>
                <th scope="col" class="text-center">nok Relation ship Code</th>
                <th scope="col" class="text-center">nok Address Line1</th>
                <th scope="col" class="text-center">nok Address Line2</th>
                <th scope="col" class="text-center">nok Address Line3</th>
                <th scope="col" class="text-center">nok Address Line4</th>
                <th scope="col" class="text-center">nok Postcode</th>
                <th scope="col" class="text-center">gp Code</th>
                <th scope="col" class="text-center">gp Surname</th>
                <th scope="col" class="text-center">gp Initials</th>
                <th scope="col" class="text-center">gp Phone</th>
                <th scope="col" class="text-center">Edit</th>
                <th scope="col" class="text-center">Delete</th>
              </tr>
            </thead>
            <tbody>
                <tr *ngFor="
                let i of Patients
                  | paginate
                    : {
                        itemsPerPage: 10,
                        currentPage: 1,
                        totalItems: 20
                      }
              ">
                <td>{{i.id}}</td>
                <td>{{i.pasNumber}}</td>
                <td>{{i.forenames}}</td>
                <td>{{i.surname}}</td>
                <td>{{i.dateOfBirth | date}}</td>
                <td>{{i.sexCode}}</td>
                <td>{{i.homeTelephoneNumber}}</td>
                <td>{{i.nokName}}</td>
                <td>{{i.nokRelationshipCode}}</td>
                <td>{{i.nokAddressLine1}}</td>
                <td>{{i.nokAddressLine2}}</td>
                <td>{{i.nokAddressLine3}}</td>
                <td>{{i.nokAddressLine4}}</td>
                <td>{{i.nokPostcode}}</td>
                <td>{{i.gpCode}}</td>
                <td>{{i.gpSurname}}</td>
                <td>{{i.gpInitials}}</td>
                <td>{{i.gpPhone}}</td>
                <td>
                    <!-- (click)="openBackDropCustomClass(holiday,i)" -->
                  <button type="button" class="btn tp-btn-light  btn-secondary"
                  >editbutton</button>
                </td>
                <td>
                    <!-- (click)="removeholiday(i.id)" -->
                  <button type="button" class="btn tp-btn-light  btn-danger"
                    >removebutton</button>
                </td>
                <td>
                </td>
                <td>

                </td>
              </tr>
            </tbody>
          </table>
          <pagination-controls
          class="pagi"
          (pageChange)="gty((page = $event))"
        ></pagination-controls>
        </div>
      </div>
    </div>
    </div>

       <!--  edit&createskill -->
